mapper.select:
select l.*,a.name adminName from lecture l left join admin a on l.admin_id= a.id
year = #{srch}
semester like concat(#{srch},'%')
l.id LIKE concat(#{srch},'%')
l.title LIKE concat(#{srch},'%')
subType LIKE concat(#{srch},'%')
credit = #{srch}
order by year desc limit ${start} , ${end}
